import { useEffect ,useState} from "react";

async function fetchData(){
  try {
    const response = await fetch('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&ids=bitcoin%2Cethereum%2Cbinancecoin%2Csolana%2Cripple%2Ccardano%2Cavalanche-2%2Cchainlink%2Cdogecoin%2Ctron%2Cpolkadot%2Cmatic-network%2Cinternet-computer%2Cuniswap%2Cstacks&order=market_cap_desc&per_page=100&page=1&sparkline=false&locale=en&precision=2&x_cg_api_key=CG-QqYtpmrbXMM9hZ1MDWeytPqg');
    const data = await response.json(); 
    return data
  } catch (error) {
    console.error(error)
  }
}


function App() {
  const [data,setData]=useState(null)
  useEffect(()=>{
    fetchData().then(data=>setData(data)).catch(err=>console.error(err))
  },[])//se executa doar cand se monteaza componenta(prima data)

  useEffect(()=>{
    console.log(data)
  },[data])

  return (
    <table>
      <tr>
        <td>Coin</td>
        <td>Price</td>
        <td>Volume</td>
        <td>Market Cap</td>
      </tr>
      {data&& data.map(element =><div>{element.name} {element.current_price}</div>)}
    </table>
  );
}

export default App;

