import { useDispatch , useSelector } from "react-redux"
import { setPushNotification } from "../../store/slices/slice"
import { useEffect, useState } from "react"
import { getNotifications } from "../../api_s/getNotifications"

function PushupNotification() {
    const dispatch = useDispatch()
    const [message , setMessage] = useState('')
    function manageNotification() {
        dispatch(setPushNotification(false))
    }

    useEffect(()=>{
        getNotifications(localStorage.getItem('token'))
        .then(data=>setMessage(data.reverse()[0].message))
        .catch(err=>console.error(err))
    },[message])
    return(
        <div className="flex items-center absolute transform top-1 left-1/2 -translate-x-1/2 bg-white text-xxs sm:text-base rounded-sm notification-slide-down">
           <img src='/icos/dot-red.svg' className="symbol" />
           <p className="flex-grow p-1 pr-2 border-r border-grey-600 max-h-full">{message}</p>
           <img src='/icos/x.svg' className="w-6 cursor-pointer p-2" onClick={()=>manageNotification()}/>
        </div>
    )
}

export default PushupNotification
