import AssetsList from "./AssetsList"
import { setCollection, setToken } from "../store/slices/slice"
import { useDispatch } from "react-redux"
import { useSelector } from "react-redux"


function Markets(){
    const dispatch=useDispatch()

    const lastUpdate=useSelector(state=>state.global.lastUpdate)
    const menu=useSelector(state=>state.global.menu)
    const notificationCenter=useSelector(state=>state.global.notificationCenter)

    if(notificationCenter) {
        document.body.classList.add('overflow-y-hidden')
    } else {
        document.body.classList.remove('overflow-y-hidden')
    }
    return(
        <div className={`fixed top-16.4 left-4/5 mt-1 rounded-md border border-black border-2  ${menu?'w-4/5':'w-full'} h-screen overflow-auto`}>
            <div className="flex items-center justify-evenly bg-white text-black rounded-tl-md rounded tr-md text-xs  sm:text-base p-2 sm:p-4">
                <p className="">Select market</p>
                {lastUpdate&&<p className=" text-right"><span className="text-xs">last updated:</span>  {lastUpdate}<span className="text-sm  sm:text-lg font-light"> UTC</span></p>}
            </div>
            <div className="flex w-full">
                <button className="bg-sky-500 hover:bg-sky-500 focus:outline-none focus:border-b-2 focus:border-black flex-grow rounded-tl-xl rounded-bl-xl text-xs  sm:text-base" onClick={()=>dispatch(setCollection('stocks'))}>Stocks</button>
                <button className="bg-sky-500 hover:bg-sky-500 focus:outline-none focus:border-b-2 focus:border-black flex-grow text-xs  sm:text-base" onClick={()=>dispatch(setCollection('commodities'))}>Commodities</button>
                <button className="bg-sky-500 hover:bg-sky-500 focus:outline-none focus:border-b-2 focus:border-black flex-grow rounded-tr-xl rounded-br-xl text-xs  sm:text-base" onClick={()=>dispatch(setCollection('cryptocurrencies'))}>Crypto</button>
            </div>
            <AssetsList/>
        </div>
    )
}
export default Markets
