import { useEffect } from 'react'
import {fetchServer} from '../api_s/fetchServer'
import {useSelector ,useDispatch} from 'react-redux'
//este nevoie de dispatch pentru a declansa actiunile de actualizare a starilor
import {setAssets,setLastUpdate,setError } from '../store/slices/slice'
import Item from './Item'

function AssetsList(){
    
    const collection=useSelector(state=>state.global.collection)
    const assets=useSelector(state=>state.global.assets)
    const error=useSelector(state=>state.global.error)

    const dispatch=useDispatch()
    //eg.: dispatch(setCollection('stocks'))
    useEffect(()=>{
        fetchServer(collection).then(el=>{
            if(el.data){
                dispatch(setAssets(el.data))
                dispatch(setLastUpdate(el.timestamp))
                dispatch(setError(null))
            }
            else{
                dispatch(setLastUpdate(null))
                dispatch(setAssets(null))
                dispatch(setError(el))
            }
        }).catch(err=>console.error(err))
    },[collection])
    return(
        <div className='flex items-center flex-col mt-2 overflow-auto w-full'>
            {!error 
                ?
                <>
                    <table className="w-full">
                        <thead className='border-b-2 border-t-2 border-black text-xs  sm:text-base '>
                            <tr>
                                <td className=' p-1 sm:p-3 text-center sm:text-left'>Name</td>
                                <td className='p-1 sm:p-3 text-center sm:text-right'>Symbol</td>
                                <td className='p-1 sm:p-3 text-center sm:text-right'>Price</td>
                                <td className='p-1 sm:p-3 text-center sm:text-right'>24h %</td>
                            </tr>
                        </thead>
                        <tbody className='pl-1 pr-1 truncate text-xs text-center sm:text-base sm:text-right'>
                            {assets&& assets.map(asset =><Item item={asset} key={asset.symbol}/>)}
                        </tbody>
                    </table>
                </>
                :
                <>
                    <div className='h-40'></div>
                    <div className='flex flex-col border rounded-lg border-8 border-red-400 p-10'>
                        <div className='flex justify-center'>
                            <img src="/errorLogo.png" className='w-10 h-10'/>
                        </div>
                        <div >
                            <p className='text-red-400'><span>ERR:</span> <span className='text-xs'>{error.message}</span></p>
                        </div>
                    </div>
                </>
            }
        </div>
    )
}
export default AssetsList
