import { useState } from "react"
import { pushNotification } from "../api_s/pushNotification"
import { useDispatch } from "react-redux"
import { setOnNotify } from "../store/slices/slice"

function Item(props){
    const {item}=props
    const [selected , setSelected] = useState(false)
    const dispatch = useDispatch()

    async function addToWatchlist() {
        setSelected(!selected)
        await pushNotification(`${item.name} ${selected ? 'added to' : 'removed from'} watchlist !`)
        dispatch(setOnNotify(true))
    }
    return(
        <tr className="border-b-2 h-4">
            <td className="p-1 sm:p-3 flex items-center"><img src={`/icos/${selected ?'filled_star.png':'star.svg'}`} className="symbol mr-1 sm:mr-2 cursor-pointer" onClick={async()=>await addToWatchlist()}/> {item.image && <img src={item.image} className="w-4 h-4 sm:w-10 sm:h-10 mr-5"/>}<>{item.name}</></td>
            <td className="p-1 sm:p-3">{item.symbol}</td>
            <td className="p-1 sm:p-3">{'$ '+(item.current_price).toLocaleString('en-US')}</td>
            <td className={`p-1 sm:p-3 ${item.price_change_percentage_24h<0 ?'text-red-400':'text-green-400'}`}>{Number(item.price_change_percentage_24h).toFixed(2).toString()+" %"}</td>
        </tr>
    )
}
export default Item
