import { useEffect } from 'react'
import {fetchServer} from '../api_s/fetchServer'
import {useSelector ,useDispatch} from 'react-redux'
//este nevoie de dispatch pentru a declansa actiunile de actualizare a starilor
import {setAssets,setLastUpdate } from '../store/slices/slice'
import Item from './Item'

function AssetsList(){
    const { collection, assets, lastUpdate } = useSelector(state => ({
        collection: state.global.collection,
        assets: state.global.assets,
        lastUpdate: state.global.lastUpdate
    }));
    const dispatch=useDispatch()
    //eg.: dispatch(setCollection('stocks'))
    useEffect(()=>{
        fetchServer(collection).then(el=>{dispatch(setAssets(el.data));dispatch(setLastUpdate(el.timestamp))}).catch(err=>console.error(err))
    },[])//se executa doar cand se monteaza componenta(prima data)
    return(
        <tbody>
            {assets&& assets.map(asset =><Item item={asset} key={asset.symbol}/>)}
            {lastUpdate&&<div>{lastUpdate}</div>}
        </tbody>
    )
}
export default AssetsList
