import { useEffect } from 'react'
import {fetchServer} from '../api_s/fetchServer'
import {useSelector ,useDispatch} from 'react-redux'
//este nevoie de dispatch pentru a declansa actiunile de actualizare a starilor
import {setAssets,setLastUpdate,setError } from '../store/slices/slice'
import Item from './Item'

function AssetsList(){
    const { collection, assets, lastUpdate ,error} = useSelector(state => ({
        collection: state.global.collection,
        assets: state.global.assets,
        lastUpdate: state.global.lastUpdate,
        error:state.global.error
    }));
    const dispatch=useDispatch()
    //eg.: dispatch(setCollection('stocks'))
    useEffect(()=>{
        fetchServer(collection).then(el=>{
            if(el.data){
                dispatch(setAssets(el.data))
                dispatch(setLastUpdate(el.timestamp))
                dispatch(setError(null))
            }
            else{
                dispatch(setLastUpdate(null))
                dispatch(setAssets(null))
                dispatch(setError(el))
            }
        }).catch(err=>console.error(err))
    },[collection])
    return(
        <div className='flex items-center flex-col'>
            {error 
                ?
                <>
                    <table className="w-full">
                        <thead>
                            <tr>
                                <td className="">Name</td>
                                <td className="">Symbol</td>
                                <td className="">Price</td>
                                <td className="">24h %</td>
                            </tr>
                        </thead>
                        <tbody>
                            {assets&& assets.map(asset =><Item item={asset} key={asset.symbol}/>)}
                        </tbody>
                    </table>
                    {lastUpdate&&<div>{lastUpdate}</div>}
                </>
                :
                <>
                    <div className='h-40'></div>
                    <div className='flex flex-col border rounded-md border-3 border-red-500'>
                        <div className='flex justify-center'>
                            <img src="/errorLogo.png" className='w-10 h-10'/>
                        </div>
                        <div >
                            <p className='text-red-400'><span>ERR:</span> your fetch limit was overreached</p>
                        </div>
                    </div>
                </>
            }
        </div>
    )
}
export default AssetsList
