import { useDispatch , useSelector} from "react-redux"
import { setCollection } from "../../store/slices/slice"
import { useState } from "react"

function MarketsButton(){
    const dispatch=useDispatch()
    const collection = useSelector(state=>state.global.collection)
    const[bg_b1,setBg_b1]=useState(false)
    const[bg_b2,setBg_b2]=useState(false)
    const[bg_b3,setBg_b3]=useState(false)
    return(
        <div className="flex w-full">
            <button id='1' className="bg-sky-500 hover:bg-sky-200  flex-grow rounded-tl-xl rounded-bl-xl text-xs  sm:text-base" onClick={()=>dispatch(setCollection('stocks'))}>Stocks</button>
            <button id='2' className="bg-sky-500 hover:bg-sky-200  flex-grow text-xs  sm:text-base" onClick={()=>dispatch(setCollection('commodities'))}>Commodities</button>
            <button id='3' className="bg-sky-500 hover:bg-sky-200  flex-grow rounded-tr-xl rounded-br-xl text-xs  sm:text-base" onClick={()=>dispatch(setCollection('cryptocurrencies'))}>Cryptocurrencies</button>
        </div>
    )
}

export default MarketsButton
