import { useEffect,useState } from "react"
import { fetchNews } from "../api_s/fetchNews"
import Navbar from "./Navbar"
import { useSelector } from "react-redux"
import Menu from "./Menu"
import New from "./New"

function News(){
    const [err,setErr]=useState(null)
    const [data,setData]=useState(null)
    const menu=useSelector(state=>state.global.menu)

    useEffect(()=>{
        fetchNews().then(data=>{
            if(data.feed){
                setData(data)
            }
            else{
                throw new Error(data.message)
            }
        }).catch(error=>{
            setErr(error)
            console.error(error)
        })
    },[])
    return(
        <>
            {/* <Navbar/> */}
            <div className="flex">
                {menu&&<Menu/>}
                <div className={`w-${menu?'4/5':'full'} flex flex-col items-center m-1 rounded-md`}>                     
                    {data?.feed?
                        data.feed.map(element=><New item={element}/>)
                        // <New item={data.feed[0]}/>
                        :
                        <div>
                            {err?.message}
                        </div>
                    }
                </div>
            </div>
        </>
    )
}
export default News
