//Utilizarea variabilelor de mediu in aplicatii React:
//nu trebuie importat modulul dotenv si nici configurat(folosind .config()), ci trebuie doar instalat
//variabilele de mediu React trebuie preficate cu 'REACT_APP_'
async function fetchCoins(){
    try {
      const key=process.env.REACT_APP_COINGECKO_API_KEY
      const url=process.env.REACT_APP_COINGECKO_BASE_URL
      const response = await fetch(`${url}coins/markets?vs_currency=usd&ids=bitcoin%2Cethereum%2Cbinancecoin%2Csolana%2Cripple%2Ccardano%2Cavalanche-2%2Cchainlink%2Cdogecoin%2Ctron%2Cpolkadot%2Cmatic-network%2Cinternet-computer%2Cuniswap&order=market_cap_desc&per_page=100&page=1&sparkline=false&locale=en&precision=2&x_cg_api_key=${key}`);
      const data = await response.json();
      return data
    } catch (error) {
      console.error(error)
    }
}

export {fetchCoins}
