import { useSelector } from "react-redux"
import { useEffect, useState } from "react"
import { fetchCalendarEventsByDay } from "../api_s/fetchCalendarEventsByDay"
import CalendarEvent from "./CalendarEvent"

function Calendar(){
    const[targetDay , setTargetDay] = useState(Date.now())
    const [events , setEvents] = useState([])
    const menu=useSelector(state=>state.global.menu)

    function lastDay(){
        const day = targetDay - 24*60*60*1000
        setTargetDay(day)
    }

    function nextDay(){
        const day = targetDay + 24*60*60*1000
        setTargetDay(day)
    }

    function toDd_Mm_YyyyFormat(ndate){
        const date = new Date(ndate)
        const day = date.getDate().toString().padStart(2,'0')
        const month = (date.getMonth()+1).toString().padStart(2,'0')
        const year = date.getFullYear().toString()
        return `${day}-${month}-${year}`
    }

    useEffect(()=>{
        fetchCalendarEventsByDay(targetDay).then(data =>{ 
            setEvents(data)
        })
        .catch(err=>console.error(err))
    },[targetDay])

    return(
        <div className={`w-${menu?'4/5':'full'} flex flex-col items-center m-1 rounded-md`}>
            <div className="flex w-1/2">
                <img src='/icos/arrow-left.svg' className="symbol cursor-pointer" onClick={()=>lastDay()}/>
                <div className="flex-grow text-center">
                    {toDd_Mm_YyyyFormat(targetDay)}
                </div>
                <img src='/icos/arrow-right.png' className="symbol cursor-pointer" onClick={()=>nextDay()}/>
            </div>                
           {events.length !== 0 ?
            events.map(ev => <CalendarEvent event={ev} key={ev._id}/>)
            :
            <div>
                Eroare
            </div>
            }
        </div>
    )
}

export default Calendar
