import { useEffect, useState } from "react"
import Table from "./layout/Table"
import { getWatchlistAssets } from "../api_s/getWatchlistAssets"

function Watchlist(){
    const [err,setErr] = useState(null)
    const [assets,setAssets] = useState([])

    async function fullAssetDetails(data , market) {
        const response = await fetch(`${process.env.REACT_APP_LOCAL_SERVER}stocks/symbols`,{
            method : "POST",
            headers :{
                'Content-Type' : 'application/json'
            },
            body : data.toString()
        })
    }

    useEffect(()=>{
        getWatchlistAssets('Stock',localStorage.getItem('token')).then(async (data) => setAssets(await fullAssetDetails(data,'Stock')))
        .catch(error =>{
            console.error(error)
            setErr(error.message)
        })
    },[])
    return(
        <>
            {!err ?
                <Table assets={fullAssetsDetails()}/>
            :
            <>
                //TODO
            </>
            }
        </>
    )
}

export default Watchlist
