import {Link , useNavigate} from 'react-router-dom'
import { useSelector } from 'react-redux';
import { setToken , setMenu } from '../store/slices/slice';
import { useDispatch } from 'react-redux';
import { useEffect } from 'react';


function Navbar(){
    const {token,menu}=useSelector(state=>({
        token:state.global.token,
        menu:state.global.menu
    }))
    const navigate=useNavigate()
    const dispatch=useDispatch()

    return(
        <div className="flex justify-between p-4 bg-green-600 rounded-md ml-1 mr-1">
            <div className='flex flex-col w-10 h-9 items-center justify-center cursor-pointer' onClick={()=>{token?dispatch(setMenu(!menu)):navigate('/login')}}>
                <div className='m-0.5 w-full bg-white h-2 border rounded-md'></div>
                <div className='m-0.5 w-full bg-white h-2 border rounded-md'></div>
                <div className='m-0.5 w-full bg-white h-2 border rounded-md'></div>
            </div>
            {token
            ?
                <div className='flex gap-1 sm:gap-5'>
                    <button className="" >
                        <img src="/icos/notification.svg" alt="Alerts logo" className="symbol"/>
                    </button>  
                    <button className="border border-black border-2 bg-white rounded-md  sm:p-1 text-xxs sm:text-base  w-10 sm:w-20 h-5 sm:h-auto" onClick={()=>{localStorage.removeItem('token');dispatch(setToken(null));dispatch(setMenu(false))}}>
                        Logout
                    </button> 
                </div>
            :
                <div className='flex  gap-1 sm:gap-5'>
                    <button className="" >
                        <img src="/icos/notification.svg" alt="Alerts logo" className="symbol"/>
                    </button>
                    <Link to="/login" >
                        <button className="border border-black border-2 bg-white p-1 text-md rounded-md w-20">Login</button>
                    </Link>
                </div> 
            }
        </div>
    )
}
export default Navbar
