import { useDispatch , useSelector} from "react-redux"
import { setCollection } from "../../store/slices/slice"
import { useState , useRef } from "react"

function MarketsButton(){
    const dispatch=useDispatch()
    const collection = useSelector(state=>state.global.collection)

    const b1_ref=useRef(null)
    const b2_ref=useRef(null)
    const b3_ref=useRef(null)

    const[bg_b1,setBg_b1]=useState(true)
    const[bg_b2,setBg_b2]=useState(false)
    const[bg_b3,setBg_b3]=useState(false)

    function changeCollection(market) {
        dispatch(setCollection(market))
        setBg_b1(b1_ref.current.textContent.toLowerCase() == collection)
        setBg_b2(b2_ref.current.textContent.toLowerCase() == collection)
        setBg_b3(b3_ref.current.textContent.toLowerCase() == collection)
    }

    return(
        <div className="flex w-full">
            <button id='1' ref={b1_ref} className={`${bg_b1 ? 'bg-sky-200' : 'bg-sky-500'} hover:bg-sky-200  flex-grow rounded-tl-xl rounded-bl-xl text-xs  sm:text-base`} onClick={()=>changeCollection('stocks')}>Stocks</button>
            <button id='2' ref={b2_ref} className={`${bg_b2 ? 'bg-sky-200' : 'bg-sky-500'} hover:bg-sky-200  flex-grow text-xs  sm:text-base`} onClick={()=>changeCollection('commodities')}>Commodities</button>
            <button id='3' ref={b3_ref} className={`${bg_b3 ? 'bg-sky-200' : 'bg-sky-500'} hover:bg-sky-200  flex-grow rounded-tr-xl rounded-br-xl text-xs  sm:text-base`} onClick={()=>changeCollection('cryptocurrencies')}>Cryptocurrencies</button>
        </div>
    )
}

export default MarketsButton
