import Markets from "./components/Markets";
import {BrowserRouter, Routes , Navigate ,Route ,useLocation} from "react-router-dom"
import Account from "./components/Account";
import Login from "./components/Login";
import EmailVerification from "./components/EmailVerification";
import Register from "./components/Register";
import { useSelector,useDispatch } from "react-redux";
import News from "./components/News";
import { useCheckToken } from "./custom_hooks/useCheckToken";
import { useEffect } from "react";
import { setLoading } from "./store/slices/slice";
import Navbar from "./components/Navbar";
import Menu from "./components/Menu";


function App() {
  const {token,loading,menu}=useSelector(state=>({
      token:state.global.token,
      loading:state.global.loading,
      menu:state.global.menu
  }))
  const dispatch=useDispatch()

  useCheckToken()

  const ConditionalDiv = () => {
    const location = useLocation();
    // Define the paths where you want the div to be displayed
    const pathsToShowDiv = ["/", "/news", "/account"];

    // Check if the current path is in the list of paths where the div should be shown
    if (pathsToShowDiv.includes(location.pathname)) {
      return <Navbar/>
    }
    return null;
  };

  return (
    <BrowserRouter>
      <ConditionalDiv/>

      <div className="flex mt-15">
        {menu&&<Menu/>}
        <Routes>
        {!loading&&
          <>
            <Route path="/" element={<Markets/>}/>
            <Route path="/news" element={token?<News/>:<Navigate replace to="/login"/>}/>
            <Route path="/account" element={token ? <Account /> : <Navigate replace to="/login" />} />
            <Route path='/login' element={<Login/>}/>
            <Route path='/users/verify/:id/:token' element={<EmailVerification/>}/>
            <Route path="/register" element={<Register/>}/>
          </>
        }
        </Routes>
      </div>

    </BrowserRouter>
  );
}

export default App;

//atributul replace seteaza replace=true
//aceasta inseamna ca ruta "/account" va fii inlocuita in instoricul rutelor cu ruta de /login
//a.i. , atunci cand se apasa butonul de back al browser-ului , acesta sa fie redirectionat catre o ruta
//valida, nu catre ruta ce a deviat utilizatorul catre /login , adica /account
