import { useEffect, useState } from "react"
import Table from "./layout/Table"
import { getWatchlistAssets } from "../api_s/getWatchlistAssets"

function Watchlist(){
    const [err,setErr] = useState(null)
    const [assets,setAssets] = useState([])

    async function fullAssetDetails(data , market) {
        try {
            console.log(data)
            console.log(data.map(el=>el.symbols).toString())
            const response = await fetch(`${process.env.REACT_APP_LOCAL_SERVER}stocks/symbols`,{
                method : "POST",
                headers :{
                    'Content-Type' : 'application/json'
                },
                body : {
                    "data": data.map(el=>el.symbols).toString()
                }
            })
            const result = await response.json()
            if(!response.ok) {
                throw new Error(result.message)
            }

            return result.data
        } catch (error) {
            console.error(error)
        }
    }

    useEffect(()=>{
        getWatchlistAssets('Stock',localStorage.getItem('token'))
        .then(async (data) => {
            if(data.length != 0)
                setAssets(await fullAssetDetails(data,'Stock'))
        })
        .catch(error =>{
            console.error(error)
            setErr(error.message)
        })
    },[])
    return(
        <>
            {!err ?
                <Table assets={assets}/>
            :
            <>
                //TODO
            </>
            }
        </>
    )
}

export default Watchlist
